#include <iostream>
#include <fstream>
#include "BigFloat.h" 


template <typename Func>
void measureAndRecordExecutionTime(Func func, const BigInt& num1, const BigInt& num2, const std::string& funcName, const std::string& filename) {
    auto start = std::chrono::high_resolution_clock::now();

    // Выполнение функции
    BigInt result = func(num1, num2);

    auto end = std::chrono::high_resolution_clock::now();
    std::chrono::duration<double> duration = end - start;
    double durationInSeconds = duration.count();
    double durationInMilliseconds = durationInSeconds * 1000;

    std::cout << "Execution time of " << funcName << ": " << durationInSeconds << " seconds (" << durationInMilliseconds << " milliseconds)" << std::endl;

    // Запись результата в CSV файл
    std::ofstream file(filename, std::ios::app); // Открытие файла в режиме добавления
    if (file.is_open()) {
        file << funcName << ", seconds = " << durationInSeconds << ", milliseconds = " << durationInMilliseconds << "\n";
        file.close();
    }
    else {
        std::cerr << "Unable to open file " << filename << std::endl;
    }
    result.print();
}


